/*
 * This represents displaying a group of links.
 *
 * This is an example of a convenience component that serves to organize the data hierarchy.
 * It re-emits the custom events of its child components, adding its own information to them
 * to provide for future data accessor methods.
 *
 * @author Dennis Quan
 * @author Robert Duvall
 */

// display group as a column or cards that is organized by BootstrapVue
<template>
    <div id="mapContainer"></div>
</template>

<script>
import * as L from 'leaflet';
import { GeoSearchControl, OpenStreetMapProvider } from 'leaflet-geosearch';
import '../../node_modules/leaflet/dist/leaflet.css';
export default {
    name: 'TheMap',
    props: {
        // group to be displayed
        theLat: {
            type: String,
            required: true,
        },
        theLong: {
            type: String,
            required: true,
        }
    },
    data () {
        return {
            // backing data for edits using v-model, no copy needed since Strings are immutable
            title: 'test'
            // may also need a copy group information if there is any chance it might be changed interactively
        };
    },
    methods: {
        // emit event that signals a new link is ready to be added
        // NOTE, error checking is done in the child component
        async getrides(){
            const response = await fetch('api/getLinks?lat='+this.theLat+'&long='+this.theLong);
            return response.json();
        },
        async setupmap(){
            const provider = new OpenStreetMapProvider();
            const searchControl = new GeoSearchControl({
            provider: provider,
            });
            const mymap = L.map('mapContainer').setView([this.theLat, this.theLong], 13);
            mymap.addControl(searchControl);
            L.tileLayer('https://api.mapbox.com/styles/v1/{id}/tiles/{z}/{x}/{y}?access_token=pk.eyJ1IjoibWFwYm94IiwiYSI6ImNpejY4NXVycTA2emYycXBndHRqcmZ3N3gifQ.rJcFIG214AriISLbB6B5aw', {
                maxZoom: 18,
                attribution: 'Map data &copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors, ' +
                    'Imagery © <a href="https://www.mapbox.com/">Mapbox</a>',
                id: 'mapbox/streets-v11',
                tileSize: 512,
                zoomOffset: -1
            }).addTo(mymap);
            let circle = L.circle([this.theLat, this.theLong], {
                color: 'blue',
                fillColor: 'blue',
                fillOpacity: 0.5,
                radius: 25,
            }).addTo(mymap);
            circle.bindPopup('<h4>test</h4>').openPopup();
            const thedat=await this.getrides()
            console.log(thedat)
            for(let i=1;i<thedat.length;i++){
                var polygon = L.polygon([
                [thedat[i].requestedpickuplat, thedat[i].requestedpickuplong],
                [thedat[i].requesteddropofflat, thedat[i].requesteddropofflong]
                ]).addTo(mymap);
                polygon.bindPopup('<h4>'+thedat[i].name+'</h4>').openPopup();
                let circle = L.circle([thedat[i].requestedpickuplat, thedat[i].requestedpickuplong], {
                    color: 'blue',
                    fillColor: 'blue',
                    fillOpacity: 0.5,
                    radius: 25,
                }).addTo(mymap);
                circle.bindPopup('<h4>'+thedat[i].name+'</h4>').openPopup();
                let circle2 = L.circle([thedat[i].requesteddropofflat, thedat[i].requesteddropofflong], {
                    color: 'red',
                    fillColor: 'red',
                    fillOpacity: 0.5,
                    radius: 25,
                }).addTo(mymap);
                circle2.bindPopup('<h4>'+thedat[i].name+'</h4>').openPopup();
            }
        },
        addLink (newLink) {
            console.log(`EVENT: add new item ${newLink.name} to ${this.linkGroup.title}`);
            this.$emit('new-link', this.groupId, newLink);
